package Vjezbe;

public class Converter {

	public int kilogramsToPounds(int kilograms) {
		// TODO Auto-generated method stub
		return kilograms * 2;
	}

	public int poundsToKilograms(int pounds) {
		// TODO Auto-generated method stub
		return pounds / 2;
	}

	public int inchToCentimeters(int inch) {
		// TODO Auto-generated method stub
		return inch * 3;
	}

	public int centimetersToInch(int centimeters) {
		// TODO Auto-generated method stub
		return centimeters / 3;
	}
	
}
